export interface ITelemetryEvent {
    addProperty: (property: string, value: any, isSensitive?: boolean) => ITelemetryEvent;
    addPropertyIfNotExists: (property: string, value: any, isSensitive?: boolean) => ITelemetryEvent;
    propertyExists: (property: string) => boolean;
    addMeasure: (measure: string, value: number) => void;
    addMeasureIfNotExists: (measure: string, value: number) => void;
    getCorrelationId: () => string;
    correlateWith: (otherEvent: ITelemetryEvent) => ITelemetryEvent;
    correlateWithId: (correlationId: string) => ITelemetryEvent;
    send: () => void;
}
